svg.script:

// Fungsi untuk mengambil alamat IP
function fetchIP() {
    fetch('https://api.ipify.org?format=json')
        .then(response => response.json())
        .then(data => {
            const ipTextElement = document.getElementById('ipText');
            if (ipTextElement) {
                ipTextElement.textContent = data.ip;
            }
        })
        .catch(() => {
            const ipTextElement = document.getElementById('ipText');
            if (ipTextElement) {
                ipTextElement.textContent = 'Unable to fetch IP';
            }
        });
}

// Jalankan fungsi fetchIP saat SVG dimuat
fetchIP();
